$przygoda1.='<p class="p3">Zalew Fryszerka koło Radomska, 02.05.2006';
$przygoda1.=foto_line('powroty/foto06/060502_fryszerka',3,4,'_s');
$przygoda1.='<p class="p3">Dołek koło Wielunia, 30.04.2006';
